/// <summary>
/// configure all the datatypes in use by the module.
/// </summary>
protected void loadModule()
{
    NumberSeqDatatype datatype = NumberSeqDatatype::construct();

    next loadModule();

    //integration unique id (waystar)
    datatype.parmDatatypeId(extendedtypenum(HMIntegrationUniqueID));
    datatype.parmReferenceHelp(literalstr("@Optum:WaystarUniqueId"));
    datatype.parmWizardIsContinuous(true);
    datatype.parmWizardIsManual(NoYes::No);
    datatype.parmWizardIsChangeDownAllowed(NoYes::No);
    datatype.parmWizardIsChangeUpAllowed(NoYes::No);
    datatype.parmWizardHighest(#HighestValue);
    datatype.parmSortField(10);

    datatype.addParameterType(NumberSeqParameterType::DataArea, true, false);
    this.create(datatype);

    //charge line integration unique id (UHC)
    datatype.parmDatatypeId(extendedtypenum(HMChargeLineIntegrationUniqueID));
    datatype.parmReferenceHelp(literalstr("@Optum:UHCUniqueId"));
    datatype.parmWizardIsContinuous(true);
    datatype.parmWizardIsManual(NoYes::No);
    datatype.parmWizardIsChangeDownAllowed(NoYes::No);
    datatype.parmWizardIsChangeUpAllowed(NoYes::No);
    datatype.parmWizardHighest(#HighestValue);
    datatype.parmSortField(10);

    datatype.addParameterType(NumberSeqParameterType::DataArea, true, false);
    this.create(datatype);
}
