Method.Source:
public void clicked()
{      
    MultiSelectionHelper	multiSelectionHelper;
    FormDataSource          formDatasourcePre,formDatasourcePost;
    int                     position;
    HMClaimTable            claimTable;
    Set                     strRecordSet = new Set(Types::String);
    str                     oldValue;
    HMClaimChangeLogTable   hmClaimChangeLogTable;

    formDatasourcePre = this.formRun().dataSource(FormDatasourceStr(mzkClaimManagerHMClaimsTableListPage, HMClaimTable));

    multiSelectionHelper = MultiSelectionHelper::construct();
    multiSelectionHelper.parmDatasource(formDatasourcePre);

    claimTable = multiSelectionHelper.getFirst();

    oldValue = claimTable.AgentName;

    next clicked();

    formDatasourcePost = this.formRun().dataSource(FormDatasourceStr(mzkClaimManagerHMClaimsTableListPage, HMClaimTable));

    multiSelectionHelper = MultiSelectionHelper::construct();
    multiSelectionHelper.parmDatasource(formDatasourcePost);

    claimTable = multiSelectionHelper.getFirst();

    while(claimTable)
    {
        hmClaimChangeLogTable::createClaimChangeLog(claimTable,oldValue);

        claimTable = multiSelectionHelper.getNext();
    }

    if(HMBillParameters::find().HMRelatedClaimPrioritization == NoYes::Yes)
    {
        if (Box::yesNo("@Optum:MAZOPT0107", DialogButton::No) == DialogButton::Yes)
        {
            HMClaimTable::AssignSimilarClaims(multiSelectionHelper);
        }
    }

    position = formDatasourcePost.getPosition();

    formDatasourcePost.research();
    formDatasourcePost.refresh();

    formDatasourcePost.setPosition(position);
}
