/// <summary>
///
/// </summary>
[
    SysODataActionAttribute("GetExportSuccessResults", false)
]
public static str GetExportSuccessResults(DMFExecutionId _executionId)
{
    str jsonString = '';

    HMDMFExecutionDetailsExport dmfExecutionDetailsExport;

    dmfExecutionDetailsExport = HMActiveUserListExportEntity::getExportSuccessExecutionResult(_executionId);

    jsonString = FormJsonSerializer::serializeClass(dmfExecutionDetailsExport);

    return jsonString;
}

public static HMDMFExecutionDetailsExport getExportSuccessExecutionResult(DMFExecutionId _executionId)
{
    DMFDefinitionGroupExecution       definitionGroupExecution;
    DMFExecution                      dmfExecution;
    DMFExecutionSummaryStatus         summaryStatus;
    HMDMFExecutionDetailsExport       dmfExecutionDetailsExport;
    HMDMFExecutionDetailsExportEntity dmfExecutionDetailsExportEntity;
    List                              entityList;

    select firstonly dmfExecution
            where dmfExecution.ExecutionId == _executionId;

    dmfExecutionDetailsExport = new HMDMFExecutionDetailsExport();
    entityList                = new List(Types::Class);

    dmfExecutionDetailsExport.parmDataProjectName(dmfExecution.DefinitionGroup);
    dmfExecutionDetailsExport.parmStartDateTime(dmfExecution.getExecutionStartDateTime());
    dmfExecutionDetailsExport.parmEndDateTime(dmfExecution.getExecutionEndDateTime());
    dmfExecutionDetailsExport.parmExecutionId(_executionId);

    summaryStatus   = dmfExecution.GetExecutionSummaryStatus();

    dmfExecutionDetailsExport.parmExecutionStatus(DMFExecutionStatusHelper::GetExecutionSummaryStatusText(summaryStatus));
    dmfExecutionDetailsExport.parmErrorRecords(dmfExecution.getErrorRecordCount());

    while select definitionGroupExecution
            where definitionGroupExecution.ExecutionId == _executionId
    {
        dmfExecutionDetailsExportEntity = new HMDMFExecutionDetailsExportEntity();

        dmfExecutionDetailsExportEntity.parmEntityName(definitionGroupExecution.Entity);
        dmfExecutionDetailsExportEntity.parmEntityStatus(enum2Str(definitionGroupExecution.GetExecutionSummaryStatus()));
        dmfExecutionDetailsExportEntity.parmTotalEntityRecords(definitionGroupExecution.NoOfRecords);

        entityList.addEnd(dmfExecutionDetailsExportEntity);
    }

    dmfExecutionDetailsExport.parmEntitiesList(entityList);

    return dmfExecutionDetailsExport;
}
