public void initValue()
{
    if (!this.skipNumberSequenceCheck())
    {
        NumberSeqRecordFieldHandler::enableNumberSequenceControlForField(
            this, fieldNum(HMClaimDetailsEntity, HMClaimTable_ClaimID), HMParameters::numRefClaimNumber());
    }

    super();
}

public static void postGetStagingData(DMFDefinitionGroupExecution _dmfDefinitionGroupExecution)
{
    HMClaimDetailsV3Staging staging;
    CustTable               custTable;
    HMPatient               hmPatient;
    HMCase                  hmCase;
    HMCase                  hmCaseExist;
    HMBillParameters        hmBillParameters;
    HMCaseInsurance         hmCaseInsurance,hmCaseInsuranceExist;
    HMPatientInsurance      hmPatientInsuranceRecord;
    HMInsuranceCarrier      hmInsuranceCarrier;
    HMInvoiceType           hmInvoiceType;
    utcdatetime             currentDateTime =  DateTimeUtil::getSystemDateTime();
    HMSiteTable             hmSiteTable;
    HMICDTable              hmICDTable, hmICDTableExist;

    select firstonly HMCreateCaseOnClaimImport from hmBillParameters;

    if(hmBillParameters.HMCreateCaseOnClaimImport == NoYes::Yes)
    {

        insert_recordset hmCase (CaseNo, HMPatient)
        select CaseNo from staging
            group by staging.CaseNo,HMPatient.RecId
            where staging.DefinitionGroup == _dmfDefinitionGroupExecution.DefinitionGroup
                && staging.ExecutionId == _dmfDefinitionGroupExecution.ExecutionId
                && staging.TransferStatus == DMFTransferStatus::NotStarted
            join custTable
                where custTable.AccountNum == staging.PatientMRN
            join RecId from hmPatient
                where custTable.RecId == hmPatient.CustTable
            join hmInsuranceCarrier
                where hmInsuranceCarrier.Code == staging.InsuranceCarrierCode
            notexists join hmCaseExist
                where hmCaseExist.CaseNo == staging.CaseNo;      

        //commenting as its not needed for optum

        /*insert_recordset hmCaseInsurance(HMCase,HMPatientInsurance,InsuranceCarrier)
            select RecId from hmCase
            join RecId, InsuranceCarrier from hmPatientInsuranceRecord
            where hmCase.HMPatient == hmPatientInsuranceRecord.HMPatient
            join staging
            where staging.CaseNo == hmCase.CaseNo
                && staging.DefinitionGroup == _dmfDefinitionGroupExecution.DefinitionGroup
                && staging.ExecutionId == _dmfDefinitionGroupExecution.ExecutionId
                && staging.TransferStatus == DMFTransferStatus::NotStarted
            notexists join hmCaseInsuranceExist
            where hmCaseInsuranceExist.HMCase == hmCase.RecId
            && hmCaseInsuranceExist.HMPatientInsurance == hmPatientInsuranceRecord.RecId;*/
    }

    update_recordset staging
        setting HMPatient = hmPatient.RecId                
        where staging.DefinitionGroup == _dmfDefinitionGroupExecution.DefinitionGroup
            && staging.ExecutionId == _dmfDefinitionGroupExecution.ExecutionId
            && staging.TransferStatus == DMFTransferStatus::NotStarted
        join RecId, HMPatientsStatus from hmPatient
        join custTable
            where staging.PatientAccountNum == custTable.AccountNum
                && hmPatient.CustTable == custTable.RecId;

    update_recordset staging
        setting IsClaimModified = NoYes::Yes
                ,Patient = staging.PatientName
                ,PatientAccount = staging.PatientMRN
                ,BalanceAmount = staging.HMBalanceAmount
                ,Payor = staging.InsuranceCarrierName
        where staging.DefinitionGroup == _dmfDefinitionGroupExecution.DefinitionGroup
            && staging.ExecutionId == _dmfDefinitionGroupExecution.ExecutionId
            && staging.TransferStatus == DMFTransferStatus::NotStarted;

    update_recordset staging
        setting Version_ValidFrom = currentDateTime,
                ValidFrom = currentDateTime,
                UB04_PBM_ValidFrom = currentDateTime,
                HMICDCode1 = staging.ICD10Code1,
                HMICDCode2 = staging.ICD10Code2,
                HMICDCode3 = staging.ICD10Code3,
                HMICDCode4 = staging.ICD10Code4
        where staging.DefinitionGroup  == _dmfDefinitionGroupExecution.DefinitionGroup
            && staging.ExecutionId     == _dmfDefinitionGroupExecution.ExecutionId
            && staging.TransferStatus  == DMFTransferStatus::NotStarted;

    // update priority and membership based on the patient insurance primary key
    update_recordset staging
        setting Priority = hmPatientInsuranceRecord.Priority,
                HMInsuranceMembershipId = hmPatientInsuranceRecord.hmPatientInsuranceMembershipId
        join hmPatientInsuranceRecord
            where hmPatientInsuranceRecord.hmPatientInsurancePrimaryId  == staging.hmPatientInsurancePrimaryId
                && staging.hmPatientInsurancePrimaryId  != ''
                && staging.DefinitionGroup == _dmfDefinitionGroupExecution.DefinitionGroup
                && staging.ExecutionId == _dmfDefinitionGroupExecution.ExecutionId
                && staging.TransferStatus == DMFTransferStatus::NotStarted;

    update_recordset staging
        setting ClaimType = hmInvoiceType.ClaimType
            join hmInvoiceType
                where hmInvoiceType.Name == staging.HMInvoiceType
                    && staging.DefinitionGroup == _dmfDefinitionGroupExecution.DefinitionGroup
                    && staging.ExecutionId == _dmfDefinitionGroupExecution.ExecutionId
                    && staging.TransferStatus == DMFTransferStatus::NotStarted;

    // update SiteRegion and SiteDescription cache field on HMClaimTable
    update_recordset staging
        setting BilledRegion = hmSiteTable.HMRegion                
        join hmSiteTable
            where hmSiteTable.HMSiteName == staging.HMSite
                && staging.DefinitionGroup == _dmfDefinitionGroupExecution.DefinitionGroup
                && staging.ExecutionId == _dmfDefinitionGroupExecution.ExecutionId
                && staging.TransferStatus == DMFTransferStatus::NotStarted;

    // Create new ICD codes.
    //ICD CODE 01
    insert_recordset hmICDTable (Code)
        select ICD10Code1 from staging
            group by staging.ICD10Code1
        where staging.DefinitionGroup == _dmfDefinitionGroupExecution.DefinitionGroup
            && staging.ExecutionId == _dmfDefinitionGroupExecution.ExecutionId
            && staging.TransferStatus == DMFTransferStatus::NotStarted
            && staging.ICD10Code1 != ''
        notexists join hmICDTableExist
            where hmICDTableExist.Code == staging.ICD10Code1;

    //ICD CODE 02
    insert_recordset hmICDTable (Code)
        select ICD10Code2 from staging
            group by staging.ICD10Code2
        where staging.DefinitionGroup == _dmfDefinitionGroupExecution.DefinitionGroup
            && staging.ExecutionId == _dmfDefinitionGroupExecution.ExecutionId
            && staging.TransferStatus == DMFTransferStatus::NotStarted
            && staging.ICD10Code2 != ''
        notexists join hmICDTableExist
            where hmICDTableExist.Code == staging.ICD10Code2;

    //ICD CODE 03
    insert_recordset hmICDTable (Code)
        select ICD10Code3 from staging
            group by staging.ICD10Code3
        where staging.DefinitionGroup == _dmfDefinitionGroupExecution.DefinitionGroup
            && staging.ExecutionId == _dmfDefinitionGroupExecution.ExecutionId
        && staging.TransferStatus == DMFTransferStatus::NotStarted
        && staging.ICD10Code3 != ''
        notexists join hmICDTableExist
            where hmICDTableExist.Code == staging.ICD10Code3;

    //ICD CODE 04
    insert_recordset hmICDTable (Code)
        select ICD10Code4 from staging
            group by staging.ICD10Code4
        where staging.DefinitionGroup == _dmfDefinitionGroupExecution.DefinitionGroup
            && staging.ExecutionId == _dmfDefinitionGroupExecution.ExecutionId
            && staging.TransferStatus == DMFTransferStatus::NotStarted
            && staging.ICD10Code4 != ''
        notexists join hmICDTableExist
            where hmICDTableExist.Code == staging.ICD10Code4;

    // Claims archiving process
    HMClaimArchivingHelper claimArchivingHelper = HMClaimArchivingHelper::construct();
    claimArchivingHelper.processInit(_dmfDefinitionGroupExecution);
    // Claims archiving process

    //update_recordset staging
    //    setting 
    //        Version_ValidFrom = staging.ValidFrom,
    //        Version_ValidTo = staging.ValidTo
    //    where staging.DefinitionGroup == _dmfDefinitionGroupExecution.DefinitionGroup
    //        && staging.ExecutionId == _dmfDefinitionGroupExecution.ExecutionId
    //        && staging.TransferStatus == DMFTransferStatus::NotStarted;


    //HMPatientInsurance  hmPatientInsurance;
    //HMInsuranceCarrier  hmInsuranceCarrier;

    //update_recordset staging
    //    setting HMInsuranceCarrier = hmInsuranceCarrier.RecId
    //    where staging.DefinitionGroup == _dmfDefinitionGroupExecution.DefinitionGroup
    //        && staging.ExecutionId == _dmfDefinitionGroupExecution.ExecutionId
    //        && staging.TransferStatus == DMFTransferStatus::NotStarted
    //    join hmPatientInsurance
    //        where hmPatientInsurance.HMPatient == staging.HMPatient
    //            && hmPatientInsurance.HMPayerType == HMPayerType::InsuranceCarrier
    //            && hmPatientInsurance.InsuranceStatus == HMInsuranceStatus::Active
    //            && (hmPatientInsurance.EffectiveDate <= DateTimeUtil::getToday(DateTimeUtil::getUserPreferredTimeZone()) && hmPatientInsurance.EndDate >= DateTimeUtil::getToday(DateTimeUtil::getUserPreferredTimeZone()))
    //    join hmInsuranceCarrier
    //    join custTable
    //        where custTable.AccountNum == hmPatientInsurance.InsuranceCarrier
    //            && hmInsuranceCarrier.CustTable == custTable.RecId;

}

/// <summary>
///
/// </summary>
/// <returns></returns>
public boolean validateWrite()
{
    HMPatientInsurance  hmpPtientInsurance;
    HMPatient           hmPatient;
    CustTable           custTable;
    boolean             ret;

    ret = super();

    if(this.Priority)
    {
        if(this.InsuranceCarrierCode != '')
        {
            select firstonly RecId, HMPatient from hmpPtientInsurance
                where hmpPtientInsurance.InsuranceCarrier == this.InsuranceCarrierCode
                    && hmpPtientInsurance.Priority == this.Priority
                join RecId, CustTable from hmPatient
                    where hmPatient.RecId == hmpPtientInsurance.HMPatient
                join RecId, AccountNum from custTable
                    where custTable.RecId == hmPatient.CustTable
                        && custTable.AccountNum == this.PatientMRN;

            if(hmpPtientInsurance.RecId == 0)
            {
                return checkFailed(strFmt("Patient Insurance for Insurance Code %1 with the sequence number %2 does not exist for patient having MRN : %3",this.InsuranceCarrierCode,this.Priority,this.PatientMRN));
            }
        }
    }

    return ret;
}

/// <summary>
/// Executes the logic once after processing the target data.
/// </summary>
/// <param name= "_dmfDefinitionGroupExecution">
/// The definition group that should be processed.
/// </param>
public static void postTargetProcess(DMFDefinitionGroupExecution _dmfDefinitionGroupExecution)
{
    HMClaimTable            hmClaimTable;
    HMClaimDetailsV3Staging hmClaimDetailsStaging;

    if (_dmfDefinitionGroupExecution.StagingStatus == DMFBatchJobStatus::Finished)
    {
        try
        {
            ttsbegin;

            update_recordset hmClaimTable
         setting
            HMClaimPriority = 0
         join hmClaimDetailsStaging
            where  hmClaimTable.ClaimId                  == hmClaimDetailsStaging.HMClaimTable_ClaimID
                && hmClaimDetailsStaging.HMBalanceAmount == 0
                && hmClaimDetailsStaging.ExecutionId     == _dmfDefinitionGroupExecution.ExecutionId
                && hmClaimDetailsStaging.DefinitionGroup == _dmfDefinitionGroupExecution.DefinitionGroup
                && hmClaimDetailsStaging.TransferStatus  == DMFTransferStatus::Completed;

            ttscommit;
        }
        catch (Exception::Deadlock)
        {
            retry;
        }
        catch (Exception::UpdateConflict)
        {
            if (appl.ttsLevel() ==  0)
            {
                if (xSession::currentRetryCount() >= #RetryNum)
                {
                    throw Exception::UpdateConflictNotRecovered;
                }
                else
                {
                    retry;
                }
            }
            else
            {
                throw Exception::UpdateConflict;
            }

        }
    }
}
