Method.Source:
/// <summary>
/// Intitialize method of <c>HMClaimTable_1</c> datasource
/// </summary>
public void init()
{
    QueryBuildDataSource    qbds;
    str                     poolList;
    str                     agentList;

    super();

    this.index(indexNum(HMClaimTable, ClaimPriorityAgentPoolIdx));

    qbds = this.queryBuildDataSource();

    qbds.sortClear();
    qbds.addSortField(fieldNum(HMClaimTable, HMClaimPriority),SortOrder::Ascending);

    if (element.args().menuItemName() == menuitemDisplayStr(HMAllClaimsListPageForClaimSupervisor))
    {
        agentList = HMClaimSupervisorSelectionQueryRange::getSupervisorAgentWithEmpty();
        poolList = HMClaimSelectionQueryRange::getPersonPools();

        qbds.addRange(fieldNum(HMClaimTable, ClaimAgent)).value(agentList);

        qbds.addRange(fieldNum(HMClaimTable, ClaimPool)).value(poolList);
    }
    else if (element.args().menuItemName() == menuitemDisplayStr(HMAllClaimsListPageForClaimManager))
    {
        poolList = HMClaimSelectionQueryRange::getPersonPools();

        qbds.addRange(fieldNum(HMClaimTable, ClaimPool)).value(poolList);
    }
    else if (element.args().menuItemName() == menuitemDisplayStr(HMAllClaimsListPageForClaimAgent))
    {
        poolList = HMClaimSelectionQueryRange::getPersonPools();

        qbds.addRange(fieldNum(HMClaimTable, ClaimPool)).value(poolList);

        qbds.addRange(fieldNum(HMClaimTable, ClaimAgent)).value(strFmt('%1,%2',HMClaimSelectionQueryRange::getCurrentAgent(),0));
    }
    else if (element.args().menuItemName() == menuitemDisplayStr(HMPatientClaims))
    {
        FormRun     callerFormRun = element.args().caller();
        HMPatient   patient = callerFormRun.dataSource(formDataSourceStr(HMPatient, HMPatient)).cursor() as HMPatient;

        qbds.addRange(fieldNum(HMClaimTable, HMPatient)).value(queryValue(patient.RecId));
    }
    else if (element.args().menuItemName() == menuitemDisplayStr(HMPayorClaims))
    {
        FormRun             callerFormRun = element.args().caller();
        HMInsuranceCarrier  insuranceCarrier = callerFormRun.dataSource(formDataSourceStr(HMPayor, HMInsuranceCarrier)).cursor() as HMInsuranceCarrier;

        qbds.addRange(fieldNum(HMClaimTable, HMInsuranceCarrier)).value(queryValue(insuranceCarrier.RecId));
    }
}

/// <summary>
/// Active method of <c>HMClaimTable_1</c> datasource
/// </summary>
/// <returns>Returns the boolean of active</returns>
public int active()
{
    HMAdjudicationHeader AdjudicationHeader;
    HMClaimTableIntegrationStatusData hmClaimTableIntegrationStatusData;
    HMClaimChargeLineIntegrationStatusData hmClaimChargeLineIntegrationStatusData;
    int ret;

    ret = super();

    //AdjudicationHeader = HMAdjudicationHeader::find(HMClaimTable_1.ClaimID);
    hmClaimTableIntegrationStatusData = HMClaimTableIntegrationStatusData::find(HMClaimTable_1.ClaimID); 

    //if (AdjudicationHeader.RecId)
    //{
    //    HMAdjudicationHeaderBtn.enabled(true);
    //}
    //else if (!AdjudicationHeader.RecId)
    //{
    //    HMAdjudicationHeaderBtn.enabled(false);
    //}
    //else 
    if (hmClaimTableIntegrationStatusData.RecId)
    {
        WaystarStatusBtn.enabled(true);
        UHCStatusBtn.enabled(false);
    }
    else
    {
        WaystarStatusBtn.enabled(false);

        hmClaimChargeLineIntegrationStatusData = HMClaimChargeLineIntegrationStatusData::findByClaimId(HMClaimTable_1.ClaimID);

        if (hmClaimChargeLineIntegrationStatusData.RecId)
        {
            UHCStatusBtn.enabled(true);
        }
        else
        {
            UHCStatusBtn.enabled(false);
        }
    }

    return ret;
}

/// <summary>
/// Clicked event of Action and notes button
/// </summary>
public void clicked()
{
    CustCollectionsAgent curCustCollectionsAgent = CustCollectionsAgent::findCurrentAgent();

    if(curCustCollectionsAgent.RecId != 0 && curCustCollectionsAgent.HMIsManager == NoYes::No)
    {
        CustCollectionsAgent    checkCustCollectionsAgent;
        MultiSelectionHelper    helper;
        HMClaimTable            hmClaimTable;
        DirPartyRelationship    dirPartyRelationship;
        DirPartyTable           dirPartyTable;

        helper = MultiSelectionHelper::construct();
        helper.parmDatasource(HMClaimTable_1_ds);
        hmClaimTable = helper.getFirst();

        while (hmClaimTable.RecId != 0)
        {
            select firstonly RecId, Name from dirPartyTable
            join dirPartyRelationship
                where dirPartyRelationship.ChildParty == dirPartyTable.RecId
                    && dirPartyRelationship.RecId == curCustCollectionsAgent.DirPartyRelationship
            exists join checkCustCollectionsAgent
                where checkCustCollectionsAgent.HMSupervisor == dirPartyTable.RecId;

            if(!dirPartyTable.RecId && hmClaimTable.ClaimAgent == 0)
            {
                throw error('Cannot apply actions and notes on claims not picked.');
            }

            hmClaimTable = helper.getNext();
        }
    }

    super();
}

/// <summary>
/// JumpRef for Claim ID string control
/// </summary>
public void jumpRef()
{
    Args            args = new Args();
    HMClaimTable	_hmClaimTable;
    MenuFunction    menuFunction;

    super();

    select firstonly _hmClaimTable
        where _hmClaimTable.ClaimID == this.text();

    args.lookupRecord(_hmClaimTable);
    args.caller(element);

    menuFunction = new MenuFunction(menuItemDisplayStr(HMAllClaims), MenuItemType::Display);
    menuFunction.run(args);

}

/// <summary>
/// JumpRef for Case No string control
/// </summary>
public void jumpRef()
{
    Args            args = new Args();
    MenuFunction    menuFunction;
    HMCase          _hmCase;

    super();

    select firstonly _hmCase
        where _hmCase.CaseNo == this.text();

    args.record(_hmCase);
    args.lookupRecord(_hmCase);
    args.caller(element);

    menuFunction = new MenuFunction(menuItemDisplayStr(HMAllCaseListPage), MenuItemType::Display);
    menuFunction.run(args);
}

/// <summary>
/// JumpRef for Patient Account Num string control
/// </summary>
public void jumpRef()
{
    Args            args = new Args();
    MenuFunction    menuFunction;
    HMPatient       patient;
    HMClaimTable    hmClaimTable = HMClaimTable_1_ds.cursor();

    select firstonly patient
        where patient.RecId == hmClaimTable.HMPatient;

    args.record(patient);
    args.lookupRecord(patient);
    args.caller(element);

    menuFunction = new MenuFunction(menuItemDisplayStr(HMAllPatientsListPage), MenuItemType::Display);
    menuFunction.run(args);
}

