Method.Source:
[Form]
public class HMChangePriorityStrategy extends FormRun
{
    #OCCRetryCount
    CustCollectionsPool custCollectionsPoolGlobal;

}

/// <summary>
///
/// </summary>
public void closeOk()
{
    FormRun             formRun = element.args().caller();
    formDataSource      fds;
    int                 position;
    CustCollectionsPool custCollectionsPool;
    HMPriorityStrategy  hmPriorityStrategy;

    if (HMPriorityStrategyID.text() != '')
    {
        select firstonly RecId from hmPriorityStrategy
                where hmPriorityStrategy.HMPriorityStrategyID == HMPriorityStrategyID.text();

        if (hmPriorityStrategy.RecId == 0)
        {
            throw Error("@Optum:HMPriorityStrategyIDNotExistErrMsg");
        }

        select firstonly RecId from custCollectionsPool
            where custCollectionsPool.HMSequenceNumber == custCollectionsPoolGlobal.HMSequenceNumber
                && custCollectionsPool.HMPriorityStrategyID == HMPriorityStrategyID.text();

        if (custCollectionsPool.RecId != 0)
        {
            throw Error(strFmt("@Optum:HmPriorityStrategyExistSeqNumErrMsg",
                HMPriorityStrategyID.text(), custCollectionsPoolGlobal.HMSequenceNumber));
        }
        else
        {
            custCollectionsPool.clear();

            select firstonly forupdate custCollectionsPool
                where custCollectionsPool.RecId == custCollectionsPoolGlobal.RecId;
            try
            {
                ttsbegin;
                custCollectionsPool.HMPriorityStrategyID = HMPriorityStrategyID.text();
                custCollectionsPool.update();
                ttscommit;
            }
            catch (Exception::Deadlock)
            {
                retry;
            }
            catch (Exception::UpdateConflict)
            {
                if (appl.ttsLevel() ==  0)
                {
                    if (xSession::currentRetryCount() >= #RetryNum)
                    {
                        throw Exception::UpdateConflictNotRecovered;
                    }
                    else
                    {
                        retry;
                    }
                }
                else
                {
                    throw Exception::UpdateConflict;
                }

            }

            fds = formRun.dataSource(1);
            position = fds.getPosition();

            fds.refresh();
            fds.research();
            fds.setPosition(position);
        }
    }
    else
    {
        error(strFmt("@Optum:MAZOPT0090","@Optum:HMPriorityStrategyID"));

        return;
    }
    super();
}

/// <summary>
///
/// </summary>
public void init()
{
    super();
    custCollectionsPoolGlobal = element.args().record();
}

/// <summary>
/// Lookup method for the priority strategy
/// </summary>
public void lookup()
{
    Query                   query = new Query();
    QueryBuildDataSource    qbds;

    SysTableLookup sysTableLookup = SysTableLookup::newParameters(tableNum(HMPriorityStrategy), this);

    sysTableLookup.addLookupField(fieldNum(HMPriorityStrategy, HMPriorityStrategyID));
    sysTableLookup.addLookupField(fieldNum(HMPriorityStrategy, HMPriorityStrategyDescription));

    qbds = query.addDataSource(tableNum(HMPriorityStrategy));

    sysTableLookup.parmQuery(query);

    sysTableLookup.performFormLookup();

}
