/// <summary>
/// This method is used to update manual sequence number on a desired sequence number.
/// </summary>
/// <param name = "_fieldIdToCheck">_fieldIdToCheck</param>
public void updateManualResequencing(CustCollectionsPool _custCollectionsPool)
{
    DialogButton        diagBut;
    str                 strMessage = "@Optum:DialogBoxMessage";
    str                 strTitle = "Title";

    CustCollectionsPool custCollectionsPoolCur, custCollectionsPool;
    real                oldValue = _custCollectionsPool.HMSequenceNumber;
    real                newValue = HMSequenceNumber.realValue();
    boolean             sequenceUpdated;

    custCollectionsPoolCur = _custCollectionsPool;

    if (newValue < 1 )
    {
        newValue = oldValue;
        throw warning("@Optum:wrongInputWarning");
    }

    else
    {
        select RecId from custCollectionsPool
            where custCollectionsPool.HMSequenceNumber == newValue;

        if (custCollectionsPool.RecId != 0)
        {
            diagBut = Box::yesNo(strFmt(strMessage, newValue), DialogButton::No, strTitle);

            if (diagBut == DialogButton::Yes)
            {
                HMCustCollectionsPoolSequencing::updateSequencing(newValue, oldValue);
            }
            else
            {
                return;
            }

            try
            {
                ttsbegin;
                custCollectionsPoolCur.HMSequenceNumber = newValue;
                custCollectionsPoolCur.update();
                ttscommit;
            }

            catch (Exception::Deadlock)
            {
                retry;
            }
            catch (Exception::UpdateConflict)
            {
                if (appl.ttsLevel() ==  0)
                {
                    if (xSession::currentRetryCount() >= #RetryNum)
                    {
                        throw Exception::UpdateConflictNotRecovered;
                    }
                    else
                    {
                        retry;
                    }
                }
                else
                {
                    throw Exception::UpdateConflict;
                }

            }

        }

        else
        {
            try
            {
                ttsbegin;
                custCollectionsPoolCur.HMSequenceNumber = newValue;
                custCollectionsPoolCur.update();
                ttscommit;
            }

            catch (Exception::Deadlock)
            {
                retry;
            }
            catch (Exception::UpdateConflict)
            {
                if (appl.ttsLevel() ==  0)
                {
                    if (xSession::currentRetryCount() >= #RetryNum)
                    {
                        throw Exception::UpdateConflictNotRecovered;
                    }
                    else
                    {
                        retry;
                    }
                }
                else
                {
                    throw Exception::UpdateConflict;
                }

            }
        }
    }
}

///// <summary>
/////
///// </summary>
public void closeOk()
{
    FormRun                 formRun = element.args().caller();
    formDataSource          fds;
    RecId                   custCollectionsPoolRecId;
    CustCollectionsPool     custCollectionsPoolRecord;
    int                     position;

    if(HMSequenceNumber.realValue() <= 0)
    {
        error(strFmt("@Optum:MAZOPT0090","@MAZ:HMSequenceNumberLabel"));
        return;
    }

    element.updateManualResequencing(custCollectionsPoolGlobal);

    fds = formRun.dataSource(1);
    custCollectionsPoolRecord       = fds.Cursor();
    custCollectionsPoolRecId        = custCollectionsPoolRecord.RecId;

    super();

    fds.refresh();
    fds.research();

    fds.findValue(fieldNum(CustCollectionsPool, recId), int642str(custCollectionsPoolRecId));
}

/// <summary>
///
/// </summary>
public void init()
{
    super();
    custCollectionsPoolGlobal = element.args().record();
    HMSequenceNumber.realValue(custCollectionsPoolGlobal.HMSequenceNumber);
}
