/// <summary>
/// Inserts the copied criteria priority in <c>HMClaimPriorityTable</c>
/// </summary>
public void insertPriority()
{
    HMClaimPriorityTable            hmClaimPriorityTable,hmClaimPriorityTableCR;
    FormDataSource                  fds;
    CustCollectionsPoolQueryPacked  queryPacked;

    fds = element.args().record().dataSource();

    hmClaimPriorityTableCR = fds.cursor();

    try
    {
        ttsbegin;

        hmClaimPriorityTable.clear();
        hmClaimPriorityTable.Priority               = str2Int(PriorityId.text());
        hmClaimPriorityTable.Description            = Description.text();
        hmClaimPriorityTable.HMPriorityStrategyID   = PriorityStrategy.text();
        hmClaimPriorityTable.insert();

        hmClaimPriorityTable.selectForUpdate(true);

        hmClaimPriorityTable.QueryPacked = hmClaimPriorityTableCR.QueryPacked;
        hmClaimPriorityTable.update();

        ttscommit;
    }
    catch
    {
        throw error(strFmt("@Optum:HMUnableToUpdateRecord",PriorityId.text(),PriorityStrategy.text()));
    }

    fds.refresh();
    fds.research();

    fds.findRecord(hmClaimPriorityTable);

    int position = fds.getPosition();

    fds.setPosition(position);
}

/// <summary>
/// Inserts record in the <c>HMClaimPriorityTable</c> on button OK.
/// </summary>
public void closeOk()
{  
    FormRun                 formRun = element.args().caller();
    HMClaimPriorityTable    hmClaimPriorityTable;
    HMPriorityStrategy      hmPriorityStrategy;
    formDataSource          fds;
    str                     errorMsg = '';

    fds = element.args().record().dataSource();

    hmClaimPriorityTable = fds.cursor();

    if(PriorityStrategy.text() != '')
    {
        select firstonly RecId from hmPriorityStrategy
                where hmPriorityStrategy.HMPriorityStrategyID == PriorityStrategy.text();

        if (hmPriorityStrategy.RecId == 0)
        {
            this.clearPriorityStrategyId();
            throw warning("@Optum:PriorityStrategyDoesNotExistErrorMsg");
        }
    }

    if(!Description.text() || !PriorityId.text())
    {
        errorMsg = PriorityId.text() == '' ? "@Optum:MAZOPT0074" : "@Optum:MAZOPT0081";

        throw warning(strFmt("@Optum:MAZOPT0090",errorMsg));
    }
    else
    {
        this.insertPriority();
    }
    super();
}

/// <summary>
/// Clears the Priority strategy string control.
/// </summary>
public void clearPriorityStrategyId()
{
    PriorityStrategy.text('');
}

/// <summary>
///
/// </summary>
public void init()
{
    QueryBuildDataSource    qbds;
    HMClaimPriorityTable    hmClaimPriorityTable;

    super();

    hmClaimPriorityTable = element.args().record() as HMClaimPriorityTable;

    ContentSectionGroup.caption(strFmt("@Optum:HMCopyPriorityCaption",hmClaimPriorityTable.Priority,hmClaimPriorityTable.HMPriorityStrategyID));
}

/// <summary>
/// Creates the lookup for <c>HMPriorityStrategy</c>
/// </summary>
public void lookup()
{
    Query                   query = new Query();
    QueryBuildDataSource    qbds;

    SysTableLookup sysTableLookup = SysTableLookup::newParameters(tableNum(HMPriorityStrategy), this);

    sysTableLookup.addLookupField(fieldNum(HMPriorityStrategy, HMPriorityStrategyID));
    sysTableLookup.addLookupField(fieldNum(HMPriorityStrategy, HMPriorityStrategyDescription));

    qbds = query.addDataSource(tableNum(HMPriorityStrategy));

    sysTableLookup.parmQuery(query);

    sysTableLookup.performFormLookup();
}
