Method.Source:
/// <summary>
///    Creates a record for each range in a query.
/// </summary>
/// <param name="_criteriaPacked">
///    A packed query.
/// </param>
/// <param name="_tmpCriteria">
///    A temporary table that is used to hold the ranges.
/// </param>
public static void buildClaimPriorityTmpCriteriaClaim(container _criteriaPacked, HMClaimPriorityTmpCriteria _tmpCriteria)
{
    Query       query;
    QueryFilter filter;
    int         filterCount, k;
    DictField   dictField;

    if (conlen(_criteriaPacked) > 0)
    {
        query = new Query(_criteriaPacked);
        delete_from _tmpCriteria;

        filterCount = query.queryFilterCount(query.dataSourceNo(1));

        for (k=1; k <= filterCount; k++)
        {
            filter = query.queryFilter(k, query.dataSourceNo(1));

            dictField = new DictField(filter.dataSource().table(), fieldname2id(filter.dataSource().table(), filter.field()));

            _tmpCriteria.clear();
            _tmpCriteria.Table = filter.dataSource().label();
            _tmpCriteria.Field = dictField.label();
            _tmpCriteria.Value = filter.value();
            _tmpCriteria.insert();
        }
    }
}
