public static str getSumOfPaymentPostedAmount()
{
    str sumOfPaymentPostedAmount;

    sumOfPaymentPostedAmount = strFmt(@"SELECT SUM(C.AMOUNT) FROM HMCLAIMPAYMENTPOSTING AS C WHERE C.CLAIMID = %1 AND C.TYPE = %2 AND C.DATEPOSTED = %3",
        SysComputedColumn::returnField(tableStr(HMClaimPaymentPostingView), identifierStr(HMClaimPaymentPosting), fieldStr(HMClaimPaymentPosting, ClaimId)),
        SysComputedColumn::returnField(tableStr(HMClaimPaymentPostingView), identifierStr(HMClaimPaymentPosting), fieldStr(HMClaimPaymentPosting, Type)),
        SysComputedColumn::returnField(tableStr(HMClaimPaymentPostingView), identifierStr(HMClaimPaymentPosting), fieldStr(HMClaimPaymentPosting, DatePosted)));

    return sumOfPaymentPostedAmount;
}

public static str getCountOfPaymentPosted()
{
    str countOfPaymentPosted;

    countOfPaymentPosted = strFmt(@"SELECT COUNT(C.CLAIMID) FROM HMCLAIMPAYMENTPOSTING AS C WHERE C.CLAIMID = %1 AND C.TYPE = %2 AND C.DATEPOSTED = %3",
        SysComputedColumn::returnField(tableStr(HMClaimPaymentPostingView), identifierStr(HMClaimPaymentPosting), fieldStr(HMClaimPaymentPosting, ClaimId)),
        SysComputedColumn::returnField(tableStr(HMClaimPaymentPostingView), identifierStr(HMClaimPaymentPosting), fieldStr(HMClaimPaymentPosting, Type)),
        SysComputedColumn::returnField(tableStr(HMClaimPaymentPostingView), identifierStr(HMClaimPaymentPosting), fieldStr(HMClaimPaymentPosting, DatePosted)));

    return countOfPaymentPosted;
}
